	//검색서버에서 데이터 획득 - 시멘틱 검색작업 수행.
	//protected String fetchApiData(String strUrl) {
	protected String fetchApiData(URL url) {
		String strResult = "";
		HttpURLConnection conn = null;
		try {
			//URL url = new URL(strUrl);
			conn = (HttpURLConnection) url.openConnection();
			conn.setRequestMethod("GET");
			//conn.setRequestProperty("X-Auth-Token", this.token);
			conn.setDoOutput(true);
			
			int responseCode = conn.getResponseCode();
			if (responseCode == 400) {
				System.out.println("400:: 해당 명령을 실행할 수 없음 (실행할 수 없는 상태일 때, 엘리베이터 수와 Command 수가 일치하지 않을 때, 엘리베이터 정원을 초과하여 태울 때)");
			} else if (responseCode == 401) {
				System.out.println("401:: X-Auth-Token Header가 잘못됨");
			} else if (responseCode == 500) {
				System.out.println("500:: 서버 에러, 문의 필요");
			} else { // 성공
				BufferedReader br = new BufferedReader(new InputStreamReader(conn.getInputStream()));
				StringBuilder sb = new StringBuilder();
				String line = "";
				while ((line = br.readLine()) != null) {
					sb.append(line);
				}
				
				strResult = sb.toString();
				//System.out.println(strResult);
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
		
		return strResult;
	}

	
	//올워크 채용정보 파싱.
	protected List<AiMatchingRecommendationModel> parseAllworkRecruit(String strApiData) {
		//데이터 초기화.
		List<AiMatchingRecommendationModel> recruitDataAllwork = new ArrayList<AiMatchingRecommendationModel>();

		//데이터 파싱.
		try {			
			//입력 데이터 검증.
			//System.out.println(strApiData);
			if (ApiCommonUtils.isNullOrEmpty(strApiData)) return recruitDataAllwork;

			//JSON 문자열을 Java Object로 변환.
			JSONParser parser = new JSONParser();
			
			JSONObject jsonObject = (JSONObject) parser.parse(strApiData);
			
			JSONArray data = (JSONArray) jsonObject.get("items");
			if (data == null) return recruitDataAllwork;
			for (int i = 0; i < data.size(); i++) {
				JSONObject obj = (JSONObject) data.get(i);

				AiMatchingRecommendationModel dataItem = new AiMatchingRecommendationModel();
				dataItem.parseJsonObj(categoryService, obj);
				if (ApiCommonUtils.isNullOrEmpty(dataItem.getRecommend_id())) continue;
				
				//채용마감상태와 채용마감일 등록.
				CommandMap commandMap = new CommandMap();
				commandMap.put("recruitColumn", CommonColumnUtil.getRecruitColumn());
				commandMap.put("no", dataItem.getRecommend_id());
				Map<String, Object> recruitMap = netfuItemCompanyService.selectNetfuItemCompanyMap(commandMap.getMap());
				String strBizIng = (String) recruitMap.get("bizIng");
				dataItem.setBizIng(strBizIng);
				
				//채용마감일 등록.
				String strEdate = ApiConvertorUtil.getBizEndDay(dataItem.getBizIng(), dataItem.getBiz_end_type(), dataItem.getBiz_end_day());
				dataItem.setStrEdate(strEdate);

				//채용정보 등록.
				dataItem.setId(i);
				recruitDataAllwork.add(dataItem);
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
		return recruitDataAllwork;
	}

