	protected List<AiMatchingRecommendationModel> parseWorknetRecruit(String strApiData) {
		//데이터 초기화.
		List<AiMatchingRecommendationModel> recruitDataWorknet = new ArrayList<AiMatchingRecommendationModel>();

		//데이터 파싱.
		try {
			//입력 데이터 검증.
			//System.out.println(strApiData);
			if (ApiCommonUtils.isNullOrEmpty(strApiData)) return recruitDataWorknet;
			
			//JSON 문자열을 Java Object로 변환.
			JSONParser parser = new JSONParser();
			
			JSONObject jsonObject = (JSONObject) parser.parse(strApiData);
			
			JSONArray data = (JSONArray) jsonObject.get("items");
			if (data == null) return recruitDataWorknet;
			for (int i = 0; i < data.size(); i++) {
				JSONObject obj = (JSONObject) data.get(i);

				AiMatchingRecommendationModel dataItem = new AiMatchingRecommendationModel();
				dataItem.parseJsonObj(categoryService, obj);
				if (ApiCommonUtils.isNullOrEmpty(dataItem.getRecommend_id())) continue;
				
				//채용마감상태와 채용마감일 등록.
				dataItem.setBizIng("yes");
				//채용마감일 등록.
				String strEdate = ApiConvertorUtil.getBizEndDay(dataItem.getBizIng(), dataItem.getBiz_end_type(), dataItem.getBiz_end_day());
				dataItem.setStrEdate(strEdate);

				//채용정보 등록.
				dataItem.setId(i);
				recruitDataWorknet.add(dataItem);
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
		return recruitDataWorknet;
	}

