mapper.select:
select count(*) cnt 
from netfu_concern nc, netfu_item_company nic
where 1=1
	and nc.uid = #{loginId}

		and nic.no = #{no}

	and nc.r_uid = nic.uid

mapper.insert:

insert into netfu_concern
(uid, type, sub_type, no, r_uid, wdate)
values
(
	#{uid}
	, #{type}
	, #{subType}
	, #{no}
	, #{rUid}
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
)
