mapper.select:
select 
	${recruitColumn}
from netfu_item_company nic
where 1=1
	and nic.biz_ing <> 'yes'
	and ((nic.biz_end_type ='input' and nic.biz_end_day >= CURRENT_TIMESTAMP()) or nic.biz_end_type ='get' or nic.biz_end_type ='often')

		and nic.uid = #{uid}


		and nic.info_type = #{infoType}


		and (nic.info_type = #{infoType} or nic.inid_jobform = 'netfu_34537_33276')


		and (nic.biz_type3 in ('${bizType3}') or nic.biz_type6 in ('${bizType3}') or nic.biz_type9 in ('${bizType3}'))


		and (nic.area_job3 in ('${areaJob3}') or nic.area_job6 in ('${areaJob3}') or nic.area_job9 in ('${areaJob3}'))


		and (nic.biz_area2 in ('${bizArea2}') or nic.biz_area4 in ('${bizArea2}') or nic.biz_area6 in ('${bizArea2}'))

order by ${orderField} ${orderRule}
limit #{start}, #{pageSize}

select ${recruitColumn} from netfu_item_company nic , ( select aa.* from ( select recruit.no , concat( ifnull(recruit.biz_name, ''), '|' , ifnull(recruit.biz_title, ''), '|' , ifnull(recruit.biz_address1, ''), '|' , ifnull(recruit.biz_business, ''), '|' , ifnull(recruit.biz_detail, ''), '|' , ifnull(recruit.biz_pemail, ''), '|' ) fullStr from netfu_item_company recruit where 1=1 and recruit.biz_ing <> 'yes' and ((recruit.biz_end_type ='input' and recruit.biz_end_day >= CURRENT_TIMESTAMP()) or recruit.biz_end_type ='get' or recruit.biz_end_type ='often') ) aa where 1=1 and ( aa.fullStr like '%${keyword1}%'
${keywordCondition} aa.fullStr like '%${keyword2}%'
${keywordCondition} aa.fullStr like '%${keyword3}%'
) ) keyword where 1=1 and nic.no = keyword.no order by ${orderField} ${orderRule} limit #{start}, #{pageSize}

select nic.no, 'open', nic.biz_name, nic.uid, nic.biz_title, nic.biz_ing, nic.biz_end_type, nic.biz_end_day , count(nor.no) total_appliance_cnt from netfu_item_company nic, netfu_online_recruit nor where 1=1
and nic.uid = #{companyUid}
and nic.biz_ing = 'no' and ((nic.biz_end_day >= curdate() and nic.biz_end_type ='input') or nic.biz_end_type !='input') and (nic.service1 >= curdate() or nic.service2 >= curdate() or nic.service3 >= curdate() or nic.service4 >= curdate() or nic.service5 >= curdate() or nic.service100 >= curdate())
and ((nic.biz_end_day < curdate() and nic.biz_end_type ='input') or nic.biz_ing='yes' or (nic.service1 < curdate() and nic.service2 < curdate() and nic.service3 < curdate() and nic.service4 < curdate() and nic.service5 < curdate() and nic.service100 < curdate()) )
and nic.uid = nor.to_uid and nic.no = nor.to_no and nor.to_type in ('email', 'online') group by nic.no, nic.uid, nic.biz_title, nic.biz_ing, nic.biz_end_type, nic.biz_end_day order by ${orderField} ${orderRule} limit #{start}, #{pageSize}

select 
	${recruitColumn}
from netfu_item_company nic
WHERE 1=1
	AND no = #{no}

mapper.insert:

insert into netfu_item_company
(uid, passwd, name, hphone, birth, sex, use_mail, use_sms, email, wdate, login_date, memo, addr_coord, memo_coord
, type_r2, p_chk, type, hit, a_insert, tosa180422_date, post, address1, address2)
values
(
	#{uid}
	, #{passwd}
	, #{name}
	, #{hphone}
	, #{birth}
	, #{sex}
	, #{useMail}
	, #{useSms}
	, #{email}
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s') 
	, #{memo}
	, #{addrCoord}
	, #{memoCoord}
	, #{typeR2}
	, #{pChk}
	, #{type}
	, #{hit}
	, #{aInsert}
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s') 
	, #{post}
	, #{address1}
	, #{address2}
)
