mapper.select:
select count(uid) cnt from netfu_member
where 1=1
	and uid = #{loginId}
	and passwd = #{loginPw}
	and type = #{type}

select count(uid) cnt from netfu_member
where 1=1
	and uid = #{loginId}

mapper.insert:

