	, #{passwd}
	, #{name}
	, #{hphone}
	, #{birth}
	, #{sex}
	, #{useMail}
	, #{useSms}
	, #{email}
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s') 
	, #{memo}
