mapper.select:
select count(uid) cnt from netfu_member
where 1=1
	and uid = #{loginId}
	and passwd = #{loginPw}
	and type = #{type}
	
	AND `left`  <>  'yes'

select count(uid) cnt from netfu_member
where 1=1
	
	AND `left`  <>  'yes'
	
	
		and uid = #{uid}
	
	
	
		and passwd = #{passwd}
	
	
	
		and name = #{name}
	
	
		and email = #{email}
	
	
		and type = #{type}

select * from netfu_member where name = #{name}
and email = #{email}
and (phone = #{email} OR hphone = #{email})
AND `left` <> 'yes' order by no desc limit 1

select * from netfu_member where name = #{name} AND uid = #{uid}
and email = #{email}
and (phone = #{email} OR hphone = #{email})
AND `left` <> 'yes' order by no desc limit 1

mapper.update:
update netfu_member
set passwd = #{passwd}
where name = #{name} AND uid = #{uid}
	
		and email = #{email}
	
	
		and (phone = #{email} OR hphone = #{email})
	
	AND `left`  <>  'yes'

mapper.insert:

	insert into netfu_member
	(uid, passwd, name, hphone, birth, sex, use_mail, use_sms, email, wdate, login_date, memo, addr_coord, memo_coord
	, type_r2, p_chk, type, hit, a_insert, tosa180422_date, post, address1, address2)
	values
	(
		#{uid}
		, #{passwd}
		, #{name}
		, #{hphone}
		, #{birth}
		, #{sex}
		, #{useMail}
		, #{useSms}
		, #{email}
		, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
		, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s') 
		, #{memo}
		, #{addrCoord}
		, #{memoCoord}
		, #{typeR2}
		, #{pChk}
		, #{type}
		, #{hit}
		, #{aInsert}
		, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s') 
		, #{post}
		, #{address1}
		, #{address2}
	)

mapper.update:
 
	update netfu_member
	set passwd = #{passwd}
	where uid = #{uid}
	AND `left` <> 'yes'
