mapper.select:
select count(no) cnt
from netfu_item_company nic
where 1=1
	and nic.biz_ing = 'yes'
	
		${jobCondition}
	
	
		${areaCondition}
	
	
		${careerCondition}	
	
	
		${formCondition}	
	
	
		${schoolCondition}	
	
	
		and nic.pay_type = #{payType}
	
	
		and nic.biz_pay = #{pay}

select 
	${recruitColumn}
from netfu_item_company nic
where 1=1
	and biz_ing = 'yes'
	
		${jobCondition}
	
	
		${areaCondition}
	
	
		${careerCondition}	
	
	
		${formCondition}	
	
	
		${schoolCondition}	
	
	
		and nic.pay_type = #{payType}
	
	
		and nic.biz_pay = #{pay}
	
	
		order by ${orderRule}	
	
	
		order by nic.no desc		
	
limit #{start}, #{pageSize}

select count(no) cnt
from netfu_item_resume nir
where 1=1
	and nir.inid_secret <> 'yes'
	
		${jobCondition}
	
	
		${areaCondition}
	
	
		${careerCondition}	
	
	
		${formCondition}	
	
	
		${schoolCondition}	
	
	
		and nir.pay_type = #{payType}
	
	
		and nir.inid_pay = #{pay}
	
	
		order by ${orderField} ${orderRule}

select 
	${resumeColumn}
	, nm.sex, nm.birth
from netfu_item_resume nir left outer join netfu_member nm on nir.uid = nm.uid
where 1=1
	and nir.inid_secret <> 'yes'
	
		${jobCondition}
	
	
		${areaCondition}
	
	
		${careerCondition}	
	
	
		${formCondition}	
	
	
		${schoolCondition}	
	
	
		and nir.pay_type = #{payType}
	
	
		and nir.inid_pay = #{pay}
	
	
		order by ${orderField} ${orderRule}
	
	
		order by nic.no desc		
	
limit #{start}, #{pageSize}

mapper.insert:

	insert into netfu_my_service
	(	
		uid, job1, job2, job3, job4, job5, job6, job7, job8, job9
		, area, area2, area3, area4, area5, area6, sex, age, form, school, career, pay_type, pay
	)
	values
	(	#{uid}
		, #{job1}
		, #{job2}
		, #{job3}
		, #{job4}
		, #{job5}
		, #{job6}
		, #{job7}
		, #{job8}
		, #{job9}
		, #{area}
		, #{area2}
		, #{area3}
		, #{area4}
		, #{area5}
		, #{area6}
		, #{sex}
		, #{age}
		, #{form}
		, #{school}
		, #{career}
		, #{payType}
		, #{pay}
	)


	update netfu_my_service
	set 
		job1 = #{job1}, job2 = #{job2}, job3 = #{job3}, job4 = #{job4}, job5 = #{job5}, job6 = #{job6}, job7 = #{job7}, job8 = #{job8}, job9 = #{job9}
		, area = #{area}, area2 = #{area2}, area3 = #{area3}, area4 = #{area4}, area5 = #{area5}, area6 = #{area6}
		, sex = #{sex}, age = #{age}, form = #{form}, school = #{school}, career = #{career}, pay_type = #{payType}, pay = #{pay}
	where uid = #{uid} and no = #{no}
