mapper.select:
select count(*) cnt from netfu_online_recruit
where 1=1
	
		and to_type = #{toType}
	
	and type = 'job'
	and sub_type = 'company'
	and uid = #{uid}
	and to_uid = #{toUid}
	and to_no = #{toNo}

SELECT count(nic.no) cnt
FROM netfu_online_recruit AS nor, netfu_item_resume AS nir , netfu_item_company  AS nic
WHERE 1=1
	and (
			nor.type = 'job' 
			AND nor.sub_type = 'company'
			
				
					and nor.to_type = #{applyType}
				
				
					and nor.to_type in ('email', 'online', 'mobile')
				
			
		) 
	
		and nic.no = #{recruitNo}
			
	
		
			and (nir.no = nor.to_no and nic.no = nor.from_no)
		
		
			and (nir.no = nor.from_no and nic.no = nor.to_no)
		
	
	and nor.uid = #{loginId}

SELECT nic.no recruitNo , nir.no resumeNo , nir.name name , nor.from_no , nic.biz_name , nic.biz_ing , nic.info_type , nic.biz_title , nir.inid_title , nir.inid_area1 , getCodeName(nir.inid_area1) indiArea1Name , nir.inid_last_school , nir.inid_career , nir.inid_pay , getCodeName(nir.inid_pay) inidPayName , DATE_FORMAT(nor.wdate, '%m/%d') wdate , nor.no no , nor.uid uid , nic.biz_end_type , nic.biz_end_day , nic.uid companyUid , nir.uid personUid FROM netfu_online_recruit nor, netfu_item_resume nir , netfu_item_company nic WHERE 1=1 and ( nor.type = 'job' and nor.sub_type = 'company'
and nor.to_type = #{applyType}
and nor.to_type in ('email', 'online', 'mobile')
)
and nic.no = #{recruitNo}
and (nir.no = nor.to_no and nic.no = nor.from_no)
and (nir.no = nor.from_no and nic.no = nor.to_no)
and nor.uid = #{loginId} order by nor.no desc limit #{start}, #{pageSize}

SELECT count(nor.no) cnt
FROM netfu_online_recruit AS nor
WHERE 1=1
	and (
			nor.type = 'job' 
			AND nor.sub_type = 'company'
			
				
					and nor.to_type = #{applyType}
				
				
					and nor.to_type in ('email', 'online', 'mobile')
				
			
		)  
	and nor.to_uid = #{loginId}

SELECT nic.no recruitNo , nir.no resumeNo , nor.from_no , nic.biz_name , nic.info_type , nic.biz_title , nir.inid_title , nir.inid_area1 , getCodeName(nir.inid_area1) indiArea1Name , nir.inid_last_school , nir.inid_career , nir.inid_pay , getCodeName(nir.inid_pay) inidPayName , DATE_FORMAT(nor.wdate, '%m/%d') wdate , nor.no no , nor.uid uid , nic.biz_end_day , nic.uid companyUid , nir.uid personUid , nm.p_chk , nm.photo , nm.name , nir.inid_secret FROM netfu_online_recruit nor, netfu_item_resume nir , netfu_item_company nic, netfu_member nm WHERE 1=1 and ( nor.type = 'job' and nor.sub_type = 'company'
and nor.to_type = #{applyType}
and nor.to_type in ('email', 'online', 'mobile')
)
and nic.no = #{recruitNo}
and nic.no = nor.to_no and nor.to_uid = #{loginId} and nir.no = nor.from_no and nm.uid = nor.uid order by nor.no desc limit #{start}, #{pageSize}

mapper.insert:

	insert into netfu_online_recruit
	(to_type, type, sub_type, uid, to_uid, to_no, from_no, opened, job_detail, wdate)
	values
	(
		#{toType}
		, #{type}
		, #{subType}
		, #{uid}
		, #{toUid}
		, #{toNo}
		, #{fromNo}
		, #{opened}
		, #{jobDetail}
		, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
	)
