mapper.select:
select count(uid) cnt from netfu_scrap where 1=1 and type = 'job' and sub_type in ('company', 'hdht') and uid = #{loginId}
and no = #{no}
and date_add(wdate, interval '3' month) > sysdate()

select count(uid) cnt from netfu_scrap
where 1=1
	and type = 'job'
	and sub_type in ('company', 'hdht')
	and uid = #{uid}
	and no = #{no}

mapper.insert:

insert into netfu_scrap
(uid, type, sub_type, no, r_uid, wdate)
values
(
	#{uid}
	, #{type}
	, #{subType}
	, #{no}
	, #{rUid}
	, DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d %H:%i:%s')
)
