1 = 1
AND A.no = #{id}
AND A.recruit_id = #{recruit_id}
AND A.uid = #{uid}
AND ( A.uid LIKE CONCAT('%', #{keyword}, '%') OR A.recommend_list_01 LIKE CONCAT('%', #{keyword}, '%') OR A.recommend_list_02 LIKE CONCAT('%', #{keyword}, '%') OR B.biz_name LIKE CONCAT('%', #{keyword}, '%') OR B.biz_title LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY A.no DESC
ORDER BY A.${sidx} ${sord}, A.no DESC
LIMIT #{startIdx}, #{endIdx}

