mapper.select:
SELECT A.no AS id , A.type AS type , A.sub_type AS subType , A.board_code AS boardCode , A.uid AS uid , A.nick_name AS nickName , DATE_FORMAT(A.wdate, '%Y-%m-%d') AS strWdate , A.subject AS subject , A.content AS content , A.link AS link , A.hit AS hit , A.thumbnail AS thumbnail , IFNULL(B.hphone,'') AS hphone , IFNULL(B.email, '') AS email , IFNULL(C.board_name, '') AS boardName , IFNULL(C.type, '') AS boardType FROM ${type} A LEFT JOIN netfu_member B ON A.uid = B.uid LEFT JOIN netfu_board_admin C ON A.board_code = C.board_code
A.board_code = #{boardCode}
AND A.no = #{id}
AND A.wdate >= #{fromDate}
AND A.wdate <= #{toDate}
AND ( A.subject LIKE CONCAT('%', #{keyword}, '%') OR A.content LIKE CONCAT('%', #{keyword}, '%') OR A.uid LIKE CONCAT('%', #{keyword}, '%') OR A.link LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY A.no DESC
ORDER BY A.${sidx} ${sord}, A.no DESC
ORDER BY B.${sidx} ${sord}, A.no DESC
ORDER BY B.${sidx} ${sord}, A.no DESC
LIMIT #{startIdx}, #{endIdx}

SELECT
	IFNULL(COUNT(A.no), 0)
FROM
	${type} A 

	A.board_code = #{boardCode}
	
		AND A.no = #{id}
	
	
		AND A.wdate  >=  #{fromDate}
	
	
		AND A.wdate  <=  #{toDate}
	
	
		AND ( A.subject LIKE CONCAT('%', #{keyword}, '%')
			OR A.content LIKE CONCAT('%', #{keyword}, '%')
			OR A.uid LIKE CONCAT('%', #{keyword}, '%')
			OR A.link LIKE CONCAT('%', #{keyword}, '%')
			)

mapper.insert:
INSERT INTO ${type} (
	board_code
	, type
	, sub_type
	, uid
	, nick_name
	, wdate
	, subject
	, content
	, link
	, hit
	, thumbnail
) VALUES (
	#{boardCode}
	, #{type}
	, #{subType}
	, #{uid}
	, #{nickName}
	, CURRENT_TIMESTAMP()
	, #{subject}
	, #{content}
	, #{link}
	, 0
	, #{thumbnail}
)

mapper.update:
UPDATE
	${type}
SET
	subject = #{subject}
	, content = #{content}
	, link = #{link}
	, thumbnail = #{thumbnail}
WHERE
	no = #{id}

mapper.insert:
INSERT INTO ${list[0].type} (
	board_code
