mapper.select:
SELECT A.no AS id , A.uid AS user_id , A.passwd AS user_pw , A.password_que AS password_que , A.password_chk AS password_chk , A.name AS user_name , A.phone AS phone , A.hphone AS hphone , A.birth AS birth , A.sex AS sex , A.email AS email , A.token AS token , A.push_token AS push_token , A.use_mail AS use_mail , A.use_sms AS use_sms , A.use_push AS use_push , A.post AS post , A.address1 AS address1 , A.address2 AS address2 , DATE_FORMAT(A.wdate, '%Y-%m-%d %H:%i:%s') AS strWdate , A.login_date AS last_login_at , DATE_FORMAT(A.login_date, '%Y-%m-%d %H:%i:%s') AS strLastLogin , A.home AS home , A.photo AS photo , A.p_chk AS p_chk , A.memo AS memo , A.hit AS hit , A.tosa180422 AS tosa180422 , DATE_FORMAT(A.tosa180422_date, '%Y-%m-%d %H:%i:%s') AS strTosa180422Date , A.secession AS secession , A.left AS `left` , DATE_FORMAT(A.l_date, '%Y-%m-%d %H:%i:%s') AS strLdate , A.type AS user_type , CASE WHEN A.type = 1 THEN '개인회원' WHEN A.type = 2 THEN '기업회원' ELSE '회원' END AS role_name FROM netfu_member A
1=1
AND A.left <> 'yes'
AND A.no = #{id}
AND A.uid = #{user_id}
AND A.name LIKE CONCAT('%', #{user_name}, '%')
AND (A.phone LIKE CONCAT('%', #{phone}, '%') OR A.hphone LIKE CONCAT('%', #{phone}, '%'))
AND A.type = #{user_type}
AND A.left = #{left}
ORDER BY A.no DESC
ORDER BY A.${sidx} ${sord}, A.no DESC
LIMIT #{startIdx}, #{endIdx}

SELECT A.no AS id , A.uid AS user_id , A.passwd AS user_pw , A.password_que AS password_que , A.password_chk AS password_chk , A.name AS user_name , A.phone AS phone , A.hphone AS hphone , A.birth AS birth , A.sex AS sex , A.email AS email , A.token AS token , A.push_token AS push_token , A.use_mail AS use_mail , A.use_sms AS use_sms , A.use_push AS use_push , A.post AS post , A.address1 AS address1 , A.address2 AS address2 , DATE_FORMAT(A.wdate, '%Y-%m-%d %H:%i:%s') AS strWdate , A.login_date AS last_login_at , DATE_FORMAT(A.login_date, '%Y-%m-%d %H:%i:%s') AS strLastLogin , A.home AS home , A.photo AS photo , A.p_chk AS p_chk , A.memo AS memo , A.hit AS hit , A.tosa180422 AS tosa180422 , DATE_FORMAT(A.tosa180422_date, '%Y-%m-%d %H:%i:%s') AS strTosa180422Date , A.secession AS secession , A.left AS `left` , DATE_FORMAT(A.l_date, '%Y-%m-%d %H:%i:%s') AS strLdate , A.type AS user_type , CASE WHEN A.type = 1 THEN '개인회원' WHEN A.type = 2 THEN '기업회원' ELSE '회원' END AS role_name FROM netfu_member A
1 = 1
AND A.no = #{id}
AND A.uid = #{user_id}
ORDER BY A.no DESC

SELECT
	IFNULL(COUNT(A.no), 0)
FROM
	netfu_member A 

	1=1
	
		AND A.left  <>  'yes'
	
	
		AND A.no = #{id}
	
	
		AND A.uid = #{user_id}
	
	
		AND A.name LIKE CONCAT('%', #{user_name}, '%')
	
	
		AND (A.phone LIKE CONCAT('%', #{phone}, '%') OR A.hphone LIKE CONCAT('%', #{phone}, '%'))
	
	
		AND A.type = #{user_type}
	
	
		AND A.left = #{left}

SELECT IFNULL(COUNT(A.no), 0)
FROM netfu_member A

	A.uid = #{user_id}
	
		AND A.no  <>  #{id}

SELECT
	A.uid AS user_id
FROM
	netfu_member A

	A.type = #{user_type} AND A.name = #{user_name}
	
		AND A.email = #{email}
	
	
		AND A.hphone = #{phone}
	
	AND A.left  <>  'yes'

SELECT
	IFNULL(COUNT(A.no), 0) AS count
FROM
	netfu_member A

	A.type = #{user_type} AND A.uid = #{user_id} AND A.name = #{user_name}
	
		AND A.email = #{email}
	
	
		AND A.hphone = #{hphone}
	
	AND A.left  <>  'yes'

