mapper.insert:
INSERT INTO recommend_person (
	  reg
	, prs
	, beg
	, uid
	, uno
	, name
	, birth_year
	, sex
	, schooling
	, career_main
	, work_area
	, reg_date
	, memo
) VALUES (
	CURRENT_TIMESTAMP()
	, 'P'
	, CURRENT_TIMESTAMP()
	, #{uid}
	, 0
	, #{name}
	, #{birthYear}
	, #{sex}
	, #{schooling}
	, #{careerMain}
	, #{workArea}
	, CURRENT_TIMESTAMP()
	, #{memo}
)

mapper.update:
UPDATE
	recommend_person
SET
	name = #{name}
	, reg = CURRENT_TIMESTAMP()
	, birth_year = #{birthYear}
	, sex = #{sex}
	, schooling = #{schooling}
	, career_main = #{careerMain}
	, work_area = #{workArea}
	, memo = #{memo}
WHERE
	ser = #{id}

