	, biz_business = #{biz_business}
	, biz_men = #{biz_men}
	, biz_jobfrom = #{biz_jobfrom}
	, pay_type = #{pay_type}
	, biz_pay = #{biz_pay}
	, biz_career = #{biz_career}
