mapper.select:
SELECT A.ser AS id , DATE_FORMAT(A.reg, '%Y-%m-%d %H:%i:%s') AS strReg , A.prs AS prs , A.target_ser AS companyId , A.men AS men , A.place_work AS placeWork , A.recruit_item_contents AS recruitContents , DATE_FORMAT(A.date_req, '%Y-%m-%d') AS strReqDate , A.memo AS memo , A.recruit_progress_contents AS progressContents , DATE_FORMAT(A.date_work, '%Y-%m-%d') AS strDateWork , A.biz_end_type AS bizEndType , DATE_FORMAT(A.biz_end_day, '%m-%d') AS bizEndDay , IFNULL(B.company_title, '') AS companyTitle , IFNULL(B.company_initial, '') AS companyInitial FROM recruit_item A LEFT JOIN recruit_company B ON A.target_ser = B.ser
1 = 1
AND A.prs = 'P'
AND A.ser = #{id}
AND A.target_ser = #{companyId}
AND ( A.place_work LIKE CONCAT('%', #{keyword}, '%') OR A.recruit_item_contents LIKE CONCAT('%', #{keyword}, '%') OR A.memo LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY A.ser DESC
ORDER BY A.${sidx} ${sord}, A.ser DESC
LIMIT #{startIdx}, #{endIdx}

SELECT IFNULL(COUNT(A.ser), 0) FROM recruit_item A
1 = 1
AND A.ser = #{id}
AND A.target_ser = #{companyId}
AND ( A.place_work LIKE CONCAT('%', #{keyword}, '%') OR A.recruit_item_contents LIKE CONCAT('%', #{keyword}, '%') OR A.memo LIKE CONCAT('%', #{keyword}, '%') )

mapper.insert:
INSERT INTO recruit_item (
	  reg
	, prs
	, target_ser
	, men
	, place_work
	, recruit_item_contents
	, date_req
	, memo
	, date_work
	, recruit_progress_contents
) VALUES (
	CURRENT_TIMESTAMP()
	, 'P'
	, #{companyId}
	, #{men}
	, #{place_work}
	, #{recruitContents}
	, CURRENT_TIMESTAMP()
	, #{memo}
	, #{strDateWork}
	, #{progressContents}
)

mapper.update:
UPDATE
	recruit_item
SET
	place_work = #{place_work}
	, men = #{men}
	, recruit_item_contents = #{recruitContents}
	, prs = #{prs}
	, memo = #{memo}
	, date_work = #{strDateWork}
	, recruit_progress_contents = #{progressContents}
WHERE
	ser = #{id}

