mapper.select:
SELECT A.ser AS id , DATE_FORMAT(A.reg, '%Y-%m-%d %H:%i:%s') AS strReg , A.prs AS prs , DATE_FORMAT(A.beg, '%Y-%m-%d %H:%i:%s') AS strBeg , A.company_title AS company_title , A.phone AS phone , A.recruit_item AS recruit_item , A.whereis AS whereis , A.job_type AS job_type , A.men AS men , DATE_FORMAT(A.wdate, '%Y-%m-%d') AS strWdate , A.strEdate AS strEdate , A.order_view AS order_view , A.memo AS memo , A.wantedAuthNo AS wantedAuthNo , A.salary AS salary , A.preference AS preference FROM recruit_other A
A.prs = 'P'
AND A.ser = #{id}
AND A.wdate >= #{fromWDate}
AND A.wdate <= #{toWDate}
AND A.edate >= #{fromEDate}
AND A.edate <= #{toEDate}
AND ( A.prs LIKE CONCAT('%', #{keyword}, '%') OR A.company_title LIKE CONCAT('%', #{keyword}, '%') OR A.recruit_item LIKE CONCAT('%', #{keyword}, '%') OR A.whereis LIKE CONCAT('%', #{keyword}, '%') OR A.job_type LIKE CONCAT('%', #{keyword}, '%') OR A.memo LIKE CONCAT('%', #{keyword}, '%') OR A.salary LIKE CONCAT('%', #{keyword}, '%') OR A.preference LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY A.order_view ASC, A.ser DESC
ORDER BY A.${sidx} ${sord}, A.ser DESC
LIMIT #{startIdx}, #{endIdx}

SELECT
	IFNULL(COUNT(A.ser), 0)
FROM
	recruit_other A 

	A.prs = 'P'
	
		AND A.ser = #{id}
	
	
		AND A.wdate  >=  #{fromWDate}
	
	
		AND A.wdate  <=  #{toWDate}
	
	
		AND A.edate  >=  #{fromEDate}
	
	
		AND A.edate  <=  #{toEDate}
	
	
		AND (
			   A.prs LIKE CONCAT('%', #{keyword}, '%')
			OR A.company_title LIKE CONCAT('%', #{keyword}, '%')
			OR A.recruit_item LIKE CONCAT('%', #{keyword}, '%')
			OR A.whereis LIKE CONCAT('%', #{keyword}, '%')
			OR A.job_type LIKE CONCAT('%', #{keyword}, '%')
			OR A.memo LIKE CONCAT('%', #{keyword}, '%')
			OR A.salary LIKE CONCAT('%', #{keyword}, '%')
			OR A.preference LIKE CONCAT('%', #{keyword}, '%')
			)

mapper.insert:
INSERT INTO recruit_other (
	  reg
	, prs
	, beg
	, company_title
	, phone
	, recruit_item
	, whereis
	, job_type
	, men
	, wdate
	, edate
	, strEdate
	, order_view
	, memo
	, wantedAuthNo
	, salary
	, preference
) VALUES (
	CURRENT_TIMESTAMP()
	, 'P'
	, CURRENT_TIMESTAMP()
	, #{company_title}
	, #{phone}
	, #{recruit_item}
	, #{whereis}
	, #{job_type}
	, #{men}
	, #{strWdate}
	, #{edate}
	, #{strEdate}
	, #{order_view}
	, #{memo}
	, #{wantedAuthNo}
	, #{salary}
	, #{preference}
)

mapper.update:
UPDATE
	recruit_other
SET
	reg = CURRENT_TIMESTAMP()
	, company_title = #{company_title}
	, phone = #{phone}
	, recruit_item = #{recruit_item}
	, whereis = #{whereis}
	, job_type = #{job_type}
	, men = #{men}
	, wdate = #{strWdate}
	, edate = #{edate}
	, strEdate = #{strEdate}
	, order_view = #{order_view}
	, memo = #{memo}
	, wantedAuthNo = #{wantedAuthNo}
	, salary = #{salary}
	, preference = #{preference}
WHERE
	ser = #{id}

