mapper.select:
SELECT IFNULL(COUNT(A.no), 0) FROM netfu_item_resume A
1 = 1
AND A.inid_secret <> 'yes'
AND A.info_type = #{info_type}
AND A.no = #{id}
AND A.uid = #{uid}
AND ( (A.inid_area1 = '' AND A.inid_area3 = '' AND A.inid_area5 = '') OR A.inid_area1 = #{search_loc} OR A.inid_area3 = #{search_loc} OR A.inid_area5 = #{search_loc} )
AND ( A.inid_type1 = #{search_biz_type} OR A.inid_type2 = #{search_biz_type} OR A.inid_type3 = #{search_biz_type} OR A.inid_type4 = #{search_biz_type} OR A.inid_type5 = #{search_biz_type} OR A.inid_type6 = #{search_biz_type} )
AND A.wdate >= #{fromDate}
AND A.wdate <= #{toDate}
AND ( A.inid_title LIKE CONCAT('%', #{keyword}, '%') OR A.uid LIKE CONCAT('%', #{keyword}, '%') OR A.name LIKE CONCAT('%', #{keyword}, '%') OR A.inid_mylskill LIKE CONCAT('%', #{keyword}, '%') OR A.inid_career LIKE CONCAT('%', #{keyword}, '%') OR A.inid_license LIKE CONCAT('%', #{keyword}, '%') OR A.inid_languages LIKE CONCAT('%', #{keyword}, '%') OR A.inid_introduce LIKE CONCAT('%', #{keyword}, '%') OR A.memo LIKE CONCAT('%', #{keyword}, '%') OR A.education2 LIKE CONCAT('%', #{keyword}, '%') OR A.career2 LIKE CONCAT('%', #{keyword}, '%') OR A.license2 LIKE CONCAT('%', #{keyword}, '%') OR A.language2 LIKE CONCAT('%', #{keyword}, '%') )

mapper.insert:
INSERT INTO netfu_item_resume (
	uid
	, name
	, inid_hphone
	, inid_email
	, inid_secret
	, inid_title
	, inid_type1
	, inid_area1
	, inid_area2
	, inid_mylskill
	, indi_condition
	, inid_jobform
	, pay_type
	, inid_pay
	, inid_introduce
	, wdate
	, u_wdate
	, inid_last_school
	, inid_career
	, inid_license
	, inid_languages
) VALUES (
	#{uid}
	, #{name}
	, #{inid_hphone}
	, #{inid_email}
	, #{inid_secret}
	, #{inid_title}
	, #{inid_type1}
	, #{inid_area1}
	, #{inid_area2}
	, #{inid_mylskill}
	, #{indi_condition}
	, #{inid_jobform}
	, #{pay_type}
	, #{inid_pay}
	, #{inid_introduce}
	, CURRENT_TIMESTAMP()
	, CURRENT_TIMESTAMP()
	, ''
	, ''
	, ''
	, ''
)

mapper.update:
UPDATE
	netfu_item_resume
SET
	memo = #{memo}
WHERE
	no = #{id}

UPDATE
	netfu_item_resume
SET
	  inid_secret = #{inid_secret}
	, name = #{name}
	, inid_hphone = #{inid_hphone}
	, inid_email = #{inid_email}
	, inid_title = #{inid_title}
	, inid_type1 = #{inid_type1}
	, inid_area1 = #{inid_area1}
	, inid_area2 = #{inid_area2}
	, inid_mylskill = #{inid_mylskill}
	, indi_condition = #{indi_condition}
	, inid_jobform = #{inid_jobform}
	, pay_type = #{pay_type}
	, inid_pay = #{inid_pay}
	, inid_introduce = #{inid_introduce}
	, u_wdate = CURRENT_TIMESTAMP()
WHERE
	no = #{id}
	
		AND uid = #{uid}

mapper.delete:
DELETE FROM netfu_item_resume
WHERE no = #{id} 
	
		AND uid = #{uid}
