mapper.select:
select count(biz_no) cnt from netfu_company
where 1=1
	and biz_no = #{bizNo}

mapper.insert:

insert into netfu_company
(uid, biz_no, biz_copy, biz_name, biz_recharger, biz_subject, biz_fonundation, biz_men, biz_list, biz_form, biz_category, biz_capital, biz_selling, biz_vision, biz_history, biz_phone, biz_fax, biz_post, biz_address1, biz_address2, biz_email, biz_home, biz_logo, photo1, photo2, photo3, photo4, manager, lat, lng, admit, order_view, kind, biz_manager)
values
(
	#{uid}
	, #{bizNo}
	, #{bizCopy}
	, #{bizName}
	, #{bizRecharger}
	, #{bizSubject}
	, #{bizFonundation}
	, #{bizMen}
	, #{bizList}
	, #{bizForm}
	, #{bizCategory}
	, #{bizCapital}
	, #{bizSelling}
	, #{bizVision}
	, #{bizHistory}
	, #{bizPhone}
	, #{bizFax}
	, #{bizPost}
	, #{bizAddress1}
	, #{bizAddress2}
	, #{bizEmail}
	, #{bizHome}
	, #{bizLogo}
	, #{photo1}
	, #{photo2}
	, #{photo3}
	, #{photo4}
	, #{manager}
	, #{lat}
	, #{lng}
	, #{admit}
	, #{orderView}
	, #{kind}
	, #{bizManager}
)
